import React from 'react';

const LanguageSelector = ({ options, selectedLanguage, onChange, disabled }) => (
  <div>
    <label className="block text-sm font-semibold text-gray-700 mb-4">Select Language</label>
    <div className="grid grid-cols-3 gap-3">
      {options.map((lang) => (
        <label key={lang.value} className="relative cursor-pointer">
          <input
            type="radio"
            name="language"
            value={lang.value}
            checked={selectedLanguage === lang.value}
            onChange={onChange}
            disabled={disabled}
            className="sr-only"
          />
          <div className={`p-4 rounded-xl border-2 transition-all duration-300 text-center ${selectedLanguage === lang.value ? "border-blue-500 bg-blue-50 shadow-lg transform scale-105" : "border-gray-200 hover:border-blue-300 hover:bg-blue-25"} ${disabled ? "cursor-not-allowed opacity-50" : ""}`}>
            <div className="text-lg font-bold text-blue-600 mb-1">{lang.symbol}</div>
            <div className="text-sm font-medium text-gray-700">{lang.label}</div>
          </div>
        </label>
      ))}
    </div>
  </div>
);

export default LanguageSelector;
