import React, { useState, useEffect } from "react";
import "./index.css";
import { convertNumberToWords } from "./services/api";

// Import Components
import BackgroundEffects from "./components/BackgroundEffects";
import ConverterCard from "./components/ConverterCard";
import NumberInput from "./components/NumberInput";
import LanguageSelector from "./components/LanguageSelector";
import ConvertButton from "./components/ConvertButton";
import ResultDisplay from "./components/ResultDisplay";
import ErrorDisplay from "./components/ErrorDisplay";

// --- Constants ---
const LANGUAGE_OPTIONS = [
  { value: "english", label: "English", symbol: "EN" },
  { value: "hindi", label: "Hindi", symbol: "हि" },
  { value: "marathi", label: "Marathi", symbol: "म" },
];

const FLOATING_SYMBOLS = [ "1", "2", "3", "4", "5", "6", "7", "8", "9", "0", "π", "∑", "∞", "√", "∫", "∂", "α", "β", "γ", "δ", "±", "×", "÷", "≈", "≠", "≤", "≥", "∴", "∵", "Ω", "λ", "μ", "σ", "φ", "ψ", "∇", "∆", ];

// --- Main App Component ---
export default function App() {
  // --- State Management ---
  const [numberInput, setNumberInput] = useState("");
  const [language, setLanguage] = useState("english");
  const [result, setResult] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [floatingNumbers, setFloatingNumbers] = useState([]);

  // --- Effects ---
  useEffect(() => {
    const generated = Array.from({ length: 25 }, (_, i) => ({
      id: i,
      number: FLOATING_SYMBOLS[Math.floor(Math.random() * FLOATING_SYMBOLS.length)],
      delay: Math.random() * 10,
      duration: Math.random() * 15 + 10,
      startX: Math.random() * 100,
      startY: Math.random() * 100,
    }));
    setFloatingNumbers(generated);
  }, []);

  // --- Event Handlers ---
  const handleConvert = async () => {
    setResult("");
    setError("");
    setIsLoading(true);

    try {
      const data = await convertNumberToWords(numberInput, language);
      setResult(data.words);
    } catch (error) {
      setError(
        error.message ||
          "Network error: Unable to connect to the server. Please check your connection and try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (numberInput.trim()) {
      handleConvert();
    }
  };

  // --- Render ---
  return (
    <div className="min-h-screen font-sans bg-[linear-gradient(135deg,#667eea_0%,#764ba2_100%)] grid place-items-center p-4 relative overflow-hidden">
      <BackgroundEffects floatingNumbers={floatingNumbers} />

      <ConverterCard>
        <form onSubmit={handleSubmit} className="space-y-6">
          <NumberInput
            value={numberInput}
            onChange={(e) => setNumberInput(e.target.value.replace(/[^0-9]/g, ""))}
            disabled={isLoading}
          />
          <LanguageSelector
            options={LANGUAGE_OPTIONS}
            selectedLanguage={language}
            onChange={(e) => setLanguage(e.target.value)}
            disabled={isLoading}
          />
          <ConvertButton
            isLoading={isLoading}
            disabled={isLoading || !numberInput.trim()}
          />
        </form>
        <ResultDisplay result={result} />
        <ErrorDisplay error={error} />
      </ConverterCard>
    </div>
  );
}
