import React, { useState, useEffect } from "react";
import { Loader2 } from "lucide-react";
import "./index.css";
import { convertNumberToWords } from "./services/api";

// --- Constants for better readability ---
const LANGUAGE_OPTIONS = [
  { value: "english", label: "English", symbol: "EN" },
  { value: "hindi", label: "Hindi", symbol: "‡§π‡§ø" },
  { value: "marathi", label: "Marathi", symbol: "‡§Æ" },
];

const FLOATING_SYMBOLS = [ "1", "2", "3", "4", "5", "6", "7", "8", "9", "0", "œÄ", "‚àë", "‚àû", "‚àö", "‚à´", "‚àÇ", "Œ±", "Œ≤", "Œ≥", "Œ¥", "¬±", "√ó", "√∑", "‚âà", "‚â†", "‚â§", "‚â•", "‚à¥", "‚àµ", "Œ©", "Œª", "Œº", "œÉ", "œÜ", "œà", "‚àá", "‚àÜ", ];

// --- Child Component ---
const FloatingNumber = ({ number, delay, duration, startX, startY }) => (
  <div
    className="absolute text-blue-200/30 font-bold pointer-events-none select-none"
    style={{
      left: `${startX}%`,
      top: `${startY}%`,
      fontSize: `${Math.random() * 20 + 15}px`,
      animation: `float ${duration}s infinite linear`,
      animationDelay: `${delay}s`,
    }}
  >
    {number}
  </div>
);

// --- Main App Component ---
export default function App() {
  const [numberInput, setNumberInput] = useState("");
  const [language, setLanguage] = useState("english");
  const [result, setResult] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [floatingNumbers, setFloatingNumbers] = useState([]);

  useEffect(() => {
    const generated = Array.from({ length: 25 }, (_, i) => ({
      id: i,
      number: FLOATING_SYMBOLS[Math.floor(Math.random() * FLOATING_SYMBOLS.length)],
      delay: Math.random() * 10,
      duration: Math.random() * 15 + 10,
      startX: Math.random() * 100,
      startY: Math.random() * 100,
    }));
    setFloatingNumbers(generated);
  }, []);

  const handleConvert = async () => {
    setResult("");
    setError("");
    setIsLoading(true);

    try {
      const data = await convertNumberToWords(numberInput, language);
      setResult(data.words);
    } catch (error) {
      // The error message now comes directly from our API service
      setError(
        error.message ||
          "Network error: Unable to connect to the server. Please check your connection and try again."
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (numberInput.trim()) {
      handleConvert();
    }
  };

  return (
    <div className="min-h-screen font-sans bg-[linear-gradient(135deg,#667eea_0%,#764ba2_100%)] grid place-items-center p-4 relative overflow-hidden">
      {floatingNumbers.map((item) => (
        <FloatingNumber key={item.id} {...item} />
      ))}

      <div className="absolute inset-0 opacity-10">
        <div
          className="w-full h-full"
          style={{
            backgroundImage: `linear-gradient(rgba(255,255,255,0.1) 1px, transparent 1px), linear-gradient(90deg, rgba(255,255,255,0.1) 1px, transparent 1px)`,
            backgroundSize: "50px 50px",
          }}
        />
      </div>

      <div className="w-full max-w-md bg-white/95 backdrop-blur-[20px] border border-white/20 shadow-[0_25px_45px_rgba(0,0,0,0.1)] rounded-2xl p-8 relative z-10">
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full mb-4">
            <span className="text-2xl font-bold text-white">‚àë</span>
          </div>
          <h1 className="text-3xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
            Multilingual Number to Word Converter
          </h1>
          <p className="text-gray-600 mt-2 text-sm">Transform numbers into beautiful words</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label htmlFor="number-input" className="block text-sm font-semibold text-gray-700 mb-2">Enter Number</label>
            <div className="relative">
              <input
                id="number-input"
                type="text"
                value={numberInput}
                onChange={(e) => setNumberInput(e.target.value.replace(/[^0-9]/g, ""))}
                placeholder="e.g., 12345"
                disabled={isLoading}
                className="w-full px-4 py-4 border-2 border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all duration-300 disabled:bg-gray-100 disabled:cursor-not-allowed text-lg font-medium shadow-inner"
              />
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400">
                <span className="text-xl">123</span>
              </div>
            </div>
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-4">Select Language</label>
            <div className="grid grid-cols-3 gap-3">
              {LANGUAGE_OPTIONS.map((lang) => (
                <label key={lang.value} className="relative cursor-pointer">
                  <input type="radio" name="language" value={lang.value} checked={language === lang.value} onChange={(e) => setLanguage(e.target.value)} disabled={isLoading} className="sr-only" />
                  <div className={`p-4 rounded-xl border-2 transition-all duration-300 text-center ${language === lang.value ? "border-blue-500 bg-blue-50 shadow-lg transform scale-105" : "border-gray-200 hover:border-blue-300 hover:bg-blue-25"} ${isLoading ? "cursor-not-allowed opacity-50" : ""}`}>
                    <div className="text-lg font-bold text-blue-600 mb-1">{lang.symbol}</div>
                    <div className="text-sm font-medium text-gray-700">{lang.label}</div>
                  </div>
                </label>
              ))}
            </div>
          </div>

          <button type="submit" disabled={isLoading || !numberInput.trim()} className="w-full bg-[linear-gradient(45deg,#667eea,#764ba2)] animate-[pulse-glow_2s_infinite] hover:bg-[linear-gradient(45deg,#5a67d8,#6b46c1)] disabled:bg-gray-400 disabled:cursor-not-allowed text-white font-bold py-4 px-6 rounded-xl transition-all duration-300 flex items-center justify-center text-lg shadow-lg transform hover:scale-105 disabled:transform-none disabled:shadow-none disabled:animate-none">
            {isLoading ? (
              <><Loader2 className="w-6 h-6 animate-spin mr-3" />Converting Magic...</>
            ) : (
              <><span className="mr-2">‚ú®</span>Convert to Words<span className="ml-2">‚ú®</span></>
            )}
          </button>
        </form>

        <div className="mt-8">
          <label className="block text-sm font-semibold text-gray-700 mb-3 flex items-center"><span className="mr-2">üéØ</span>Result:</label>
          <div className="min-h-[80px] p-6 bg-gradient-to-r from-blue-50 to-purple-50 border-2 border-blue-100 rounded-xl shadow-inner">
            {result ? (
              <div className="text-center">
                <p className="text-xl font-bold text-gray-800 leading-relaxed mb-2">{result}</p>
                <div className="flex justify-center space-x-2 text-blue-500">
                  <span>‚ú®</span><span>üéâ</span><span>‚ú®</span>
                </div>
              </div>
            ) : (
              <div className="text-center text-gray-500 italic flex items-center justify-center h-full">
                <span className="mr-2">üîÆ</span>Your magical result will appear here...
              </div>
            )}
          </div>
        </div>

        {error && (
          <div className="mt-4 p-4 bg-red-50 border-2 border-red-200 rounded-xl shadow-inner">
            <div className="flex items-center">
