import React, { useState, useEffect } from "react";
import { Loader2 } from "lucide-react";
import "./index.css";

// --- Constants for better readability ---
const LANGUAGE_OPTIONS = [
  { value: "english", label: "English", symbol: "EN" },
  { value: "hindi", label: "Hindi", symbol: "à¤¹à¤¿" },
  { value: "marathi", label: "Marathi", symbol: "à¤®" },
];

const FLOATING_SYMBOLS = [ "1", "2", "3", "4", "5", "6", "7", "8", "9", "0", "Ï€", "âˆ‘", "âˆž", "âˆš", "âˆ«", "âˆ‚", "Î±", "Î²", "Î³", "Î´", "Â±", "Ã—", "Ã·", "â‰ˆ", "â‰ ", "â‰¤", "â‰¥", "âˆ´", "âˆµ", "Î©", "Î»", "Î¼", "Ïƒ", "Ï†", "Ïˆ", "âˆ‡", "âˆ†", ];

// --- Child Component ---
const FloatingNumber = ({ number, delay, duration, startX, startY, fontSize }) => (
  <div
    className="absolute text-secondary/60 font-bold pointer-events-none select-none"
    style={{
      left: `${startX}%`,
      top: `${startY}%`,
      fontSize: `${fontSize}px`,
      animation: `float ${duration}s infinite linear`,
      animationDelay: `${delay}s`,
    }}
  >
    {number}
  </div>
);

// --- Main App Component ---
export default function App() {
  const [numberInput, setNumberInput] = useState("");
  const [language, setLanguage] = useState("english");
  const [result, setResult] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [floatingNumbers, setFloatingNumbers] = useState([]);

  useEffect(() => {
    const generated = Array.from({ length: 25 }, (_, i) => ({
      id: i,
      number: FLOATING_SYMBOLS[Math.floor(Math.random() * FLOATING_SYMBOLS.length)],
      delay: Math.random() * 10,
      duration: Math.random() * 15 + 10,
      startX: Math.random() * 100,
      startY: Math.random() * 100,
      fontSize: Math.random() * 20 + 15,
    }));
    setFloatingNumbers(generated);
  }, []);

  const handleConvert = async () => {
    setResult("");
    setError("");
    setIsLoading(true);

    try {
      const apiUrl = `${process.env.REACT_APP_API_BASE_URL}/api/convert/`;
      const response = await fetch(apiUrl, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ number: numberInput, language }),
      });

      const data = await response.json();
      if (response.ok) {
        setResult(data.words);
      } else {
        setError(data.error || "An error occurred while converting the number");
      }
    } catch (networkError) {
      setError("Network error: Unable to connect to the server. Please check your connection and try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (numberInput.trim()) {
      handleConvert();
    }
  };

  return (
    <div className="min-h-screen font-sans bg-gradient-to-br from-primary to-secondary grid place-items-center p-4 relative overflow-hidden">
      {floatingNumbers.map((item) => (
        <FloatingNumber key={item.id} {...item} />
      ))}

      <div className="absolute inset-0 opacity-10">
        <div
          className="w-full h-full"
          style={{
            backgroundImage: `linear-gradient(rgba(160, 160, 192, 0.1) 1px, transparent 1px), linear-gradient(90deg, rgba(160, 160, 192, 0.1) 1px, transparent 1px)`,
            backgroundSize: "50px 50px",
          }}
        />
      </div>

      <div className="w-full max-w-md bg-card-bg/80 backdrop-blur-xl border border-accent/20 shadow-2xl shadow-primary rounded-2xl p-8 relative z-10">
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-accent to-fuchsia-500 rounded-full mb-4 shadow-lg shadow-accent/30">
            <span className="text-2xl font-bold text-primary">âˆ‘</span>
          </div>
          <h1 className="text-3xl font-bold text-text-main">
            Multilingual Number to Word Converter
          </h1>
          <p className="text-text-light mt-2 text-sm">Transform numbers into beautiful words</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label htmlFor="number-input" className="block text-sm font-semibold text-text-main mb-2">Enter Number</label>
            <div className="relative">
              <input
                id="number-input"
                type="text"
                value={numberInput}
                onChange={(e) => setNumberInput(e.target.value.replace(/[^0-9]/g, ""))}
                placeholder="e.g., 12345"
                disabled={isLoading}
                className="w-full px-4 py-4 bg-primary/80 border-2 border-secondary rounded-xl text-text-main focus:ring-2 focus:ring-accent focus:border-transparent outline-none transition-all duration-300 disabled:bg-primary/50 disabled:cursor-not-allowed text-lg font-medium shadow-inner"
              />
              <div className="absolute right-3 top-1/2 transform -translate-y-1/2 text-text-light/50">
                <span className="text-xl">123</span>
              </div>
            </div>
          </div>

          <div>
            <label className="block text-sm font-semibold text-text-main mb-4">Select Language</label>
            <div className="grid grid-cols-3 gap-3">
              {LANGUAGE_OPTIONS.map((lang) => (
                <label key={lang.value} className="relative cursor-pointer">
                  <input type="radio" name="language" value={lang.value} checked={language === lang.value} onChange={(e) => setLanguage(e.target.value)} disabled={isLoading} className="sr-only" />
                  <div className={`p-4 rounded-xl border-2 transition-all duration-300 text-center ${language === lang.value ? "border-accent bg-primary shadow-lg shadow-accent/20 transform scale-105" : "border-secondary hover:border-accent/50 hover:bg-primary/50"} ${isLoading ? "cursor-not-allowed opacity-50" : ""}`}>
                    <div className="text-lg font-bold text-accent mb-1">{lang.symbol}</div>
                    <div className="text-sm font-medium text-text-light">{lang.label}</div>
                  </div>
                </label>
              ))}
            </div>
          </div>

          <button type="submit" disabled={isLoading || !numberInput.trim()} className="w-full bg-accent hover:bg-accent-hover disabled:bg-secondary disabled:cursor-not-allowed text-primary font-bold py-4 px-6 rounded-xl transition-all duration-300 flex items-center justify-center text-lg shadow-lg shadow-accent/30 transform hover:scale-105 disabled:transform-none disabled:shadow-none">
            {isLoading ? (
              <><Loader2 className="w-6 h-6 animate-spin mr-3" />Converting Magic...</>
            ) : (
              <><span className="mr-2">âœ¨</span>Convert to Words<span className="ml-2">âœ¨</span></>
            )}
          </button>
        </form>

        <div className="mt-8">
          <label className="block text-sm font-semibold text-text-main mb-3 flex items-center"><span className="mr-2">ðŸŽ¯</span>Result:</label>
          <div className="min-h-[80px] p-6 bg-primary/80 border-2 border-secondary rounded-xl shadow-inner">
            {result ? (
              <div className="text-center">
                <p className="text-xl font-bold text-text-main leading-relaxed mb-2">{result}</p>
                <div className="flex justify-center space-x-2 text-accent">
                  <span>âœ¨</span><span>ðŸŽ‰</span><span>âœ¨</span>
                </div>
              </div>
            ) : (
              <div className="text-center text-text-light italic flex items-center justify-center h-full">
                <span className="mr-2">ðŸ”®</span>Your magical result will appear here...
              </div>
            )}
          </div>
        </div>

      </div>
    </div>
  );
}
